jQuery(document).ready(function(e){if(e(".gallery").length>0){e(".gallery").each(function(t){var n=e(this).attr("id");var r="#"+n;var i=e(n+" li").length;e(r).find("br").remove();var s=Array();e(this).find("dl").each(function(){size=e("img",this).attr("height");s.push(size)});var o=Math.max.apply(null,s);e(this).find("dl").each(function(){image=e("img",this).attr("src");size=e("img",this).attr("height");e(".gallery-item").css("height",o);e(this).css("background-image","url("+image+")");e(this).css("background-repeat","no-repeat");e(this).css("background-position","center center");e(this).find("dt").remove()});e(r).bxSlider({mode:"horizontal",captions:true,touchEnabled:true,autoHover:true,useCSS:false,onSlideAfter:function(t,n,i){e(".active-slide").removeClass("active-slide");e(r).find("dl").eq(i+1).addClass("active-slide")},onSliderLoad:function(){e(r).find("dl").eq(1).addClass("active-slide")}})})}})
